INSERT INTO `ucm_template` VALUES(19, 'member_subscription_double_optin', 'Sent to a user when they subscribe via your website.', '<h2>Confirm Subscription</h2>\r\n<p>Thank you for subscribing to our newsletter system. Please click the link below to confirm your subscription.</p>\r\n<p><a href="{LINK}">{LINK}</a></p>\r\n    ', 'a:2:{s:5:"EMAIL";s:23:"The users email address";s:4:"LINK";s:31:"The URL to confirm subscription";}', '0', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(20, 'member_subscription_confirmed', 'Displayed after use clicks their double opt-in link.', '<h2>Subscription Confirmed</h2>\r\n<p>Thank you for confirming your newsletter subscription.</p>\r\n    ', 'a:0:{}', '0', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(21, 'paymethod_banktransfer', 'Displayed when Bank Transfer payment method is selected.', 'Hello,<br />\nPlease make payment via Bank Transfer using the following details:<br />\n<br />\n{BANK_DETAILS}<br />\n<br />\nIf you have any questions please feel free to contact us.<br />\n<br />\nPlease <a href="{LINK}" target="_blank">click here</a> to return to your previous page.<br />\n<br />\nThank you<br />\n', 'a:0:{}', '1', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(22, 'paymethod_banktransfer_details', 'Bank transfer details for invoice payments.', 'Bank Name: <strong>Name Here</strong><br />\nBank Account: <strong>123456</strong><br />\nPayment Reference: <strong>{NAME}</strong><br />\nAmount: <strong>{AMOUNT}</strong>', 'a:0:{}', '1', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(23, 'paymethod_check', 'Displayed when Check payment method is selected.', 'Hello,<br />\nPlease make payment by Check as per the below details:<br />\n<br />\n{CHECK_DETAILS}<br />\n<br />\nIf you have any questions please feel free to contact us.<br />\n<br />\nPlease <a href="{LINK}" target="_blank">click here</a> to return to your previous page.<br />\n<br />\nThank you<br />\n', 'a:0:{}', '1', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(24, 'paymethod_check_details', 'Check details for invoice payments.', 'Payable To: <strong>Your Name Here</strong><br />\nPost To: <strong>123 Your Address, LA</strong><br />\nAmount: <strong>{AMOUNT}</strong>', 'a:0:{}', '1', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(25, 'paymethod_other', 'Displayed when this payment method is selected from invoice page.', 'Hello,<br />\nPlease make payment as per the below details:<br />\n<br />\n{OTHER_DETAILS}<br />\n<br />\nIf you have any questions please feel free to contact us.<br />\n<br />\nPlease <a href="{LINK}" target="_blank">click here</a> to return to your previous page.<br />\n<br />\nThank you<br />\n', 'a:0:{}', '1', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(26, 'paymethod_other_details', 'Details displayed on the invoice payments area.', 'Payable To: <strong>Name Here</strong><br />\nPost To: <strong>123 Your Address, LA</strong><br />\nAmount: <strong>{AMOUNT}</strong>', 'a:0:{}', '1', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(27, 'ticket_email_notify', 'Ticket Assigned: {TICKET_NUMBER}', 'Dear {STAFF_NAME},<br>\r\n<br>\r\nA support ticket has been assigned to you.<br>\r\nTicket Number: <strong>{TICKET_NUMBER}</strong><br>\r\nTicket Subject: <strong>{TICKET_SUBJECT}</strong><br>\r\nTo view this ticket please <a href="{TICKET_URL}">click here</a>.<br><br>\r\nThank you,<br><br>\r\n{FROM_NAME}\r\n', 'a:4:{s:10:"STAFF_NAME";s:10:"Staff Name";s:13:"TICKET_NUMBER";s:13:"Ticket Number";s:14:"TICKET_SUBJECT";s:14:"Ticket Subject";s:10:"TICKET_URL";s:27:"Link to ticket for customer";}', '1', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(28, 'ticket_container', 'The email sent along with all ticket replies.', '<span style="font-size:10px; color:#666666;">{REPLY_LINE}</span><br />\r\n<span style="font-size:10px; color:#666666;">Your ticket has been updated, please see the message below:</span><br />\r\n<br />\r\n<br />\r\n{MESSAGE}<br />\r\n<br />\r\n<br />\r\n<span style="font-size:10px; color:#666666;">Ticket Number: <strong>{TICKET_NUMBER}</strong></span><br />\r\n<span style="font-size:10px; color:#666666;">Ticket Status: <strong>{TICKET_STATUS}</strong></span><br />\r\n<span style="font-size:10px; color:#666666;">Your position in the support queue: <strong>{POSITION_CURRENT} out of {POSITION_ALL}</strong>.</span><br />\r\n<span style="font-size:10px; color:#666666;">Estimated time for a reply: <strong>within {DAYS} days</strong></span><br />\r\n<span style="font-size:10px; color:#666666;">You can view the status of your support query at any time by following this link:</span><br />\r\n<span style="font-size:10px; color:#666666;"><a href="{URL}" style="color:#666666;">View Ticket {TICKET_NUMBER} History Online</a></span><br />\r\n<br />\r\n', 'a:0:{}', '1', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(29, 'ticket_admin_email', 'Sent as an email to the administrator when a new ticket is created.', '{MESSAGE}<br />\r\n<br />\r\n<br />\r\n<span style="font-size:12px; color:#666666; font-weight: bold;">Ticket Details:</span><br />\r\n<span style="font-size:10px; color:#666666;">Number of messages: <strong>{MESSAGE_COUNT}</strong></span><br />\r\n<span style="font-size:10px; color:#666666;">Ticket Number: <strong>{TICKET_NUMBER}</strong></span><br />\r\n<span style="font-size:10px; color:#666666;">Ticket Status: <strong>{TICKET_STATUS}</strong></span><br />\r\n<span style="font-size:10px; color:#666666;">Position in the support queue: <strong>{POSITION_CURRENT} out of {POSITION_ALL}</strong>.</span><br />\r\n<span style="font-size:10px; color:#666666;">Estimated time for a reply: <strong>within {DAYS} days</strong></span><br />\r\n<span style="font-size:10px; color:#666666;">View the ticket: <strong>{URL_ADMIN}</strong></span><br />\r\n        ', 'a:0:{}', '1', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(30, 'ticket_autoreply', 'Sent as an email after a support ticket is received.', 'Hello,<br />\r\n<br />\r\nThank you for your email. We will reply shortly.<br />\r\n<br />\r\n        ', 'a:0:{}', '1', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(31, 'ticket_rejection', 'Email Bounced: {SUBJECT}.', 'Hello,<br />\r\n<br />\r\nPlease submit all NEW support tickets via our website by following this link:<br />\r\n{TICKET_URL}<br />\r\n<br />\r\nNew support tickets are no longer accepted via email due to high levels of spam causing delays for everyone.<br />\r\n<br />\r\nThanks,<br />\r\n<br />\r\n        ', 'a:0:{}', '1', 0, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(32, 'member_subscription_form', 'Used when a user wishes to subscribe.', '<h2>Subscribe</h2>\r\n<form action="" method="post">\r\n    <p>Please Enter Your Email Address: <input type="text" name="member[email]" value="{EMAIL}"> </p>\r\n    <p>Please Enter Your First Name: <input type="text" name="member[first_name]" value="{FIRST_NAME}"> </p>\r\n    <p>Please Enter Your Last Name: <input type="text" name="member[last_name]" value="{LAST_NAME}"> </p>\r\n    <p>Please Enter Your Business Name: <input type="text" name="member[business]" value="{BUSINESS}"> </p>\r\n    <p>Please Enter Your Phone Number: <input type="text" name="member[phone]" value="{PHONE}"> </p>\r\n    <p>\r\n    Please choose your newsletter subscription options: <br/>\r\n    {NEWSLETTER_OPTIONS}\r\n    </p>\r\n    <p><input type="submit" name="confirm" value="Subscribe"></p>\r\n</form>\r\n    ', 'a:0:{}', '0', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(33, 'member_subscription_error', 'Displayed when subscription fails (eg: missing email address).', '<h2>Subscription Error</h2>\r\n    <p>Sorry there was an error when processing your request:</p>\r\n    <p>{MESSAGE}</p>\r\n    ', 'a:1:{s:7:"MESSAGE";s:19:"Message to the user";}', '0', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(34, 'member_subscription_success', 'Displayed when subscription is successful.', '<h2>Subscription Success</h2>\r\n    <p>Thank you, subscription successful.</p>\r\n    <p>A message has been sent to your email address ({EMAIL}) to confirm your newsletter subscription.</p>\r\n    ', 'a:1:{s:5:"EMAIL";s:19:"Users email address";}', '0', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(35, 'member_update_details_success', 'Displayed when updating details is successful.', '<h2>Subscription Success</h2>\r\n    <p>Thank you, subscription details updated.</p>\r\n    <p>Your email address: ({EMAIL})</p>\r\n    ', 'a:1:{s:5:"EMAIL";s:19:"Users email address";}', '0', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(36, 'password_reset', 'Reset your password', 'Dear {NAME},<br>\r\n    <br>\r\n    Please click the link below to login and reset your password.<br><br>\r\n    <a href="{AUTO_LOGIN_URL}">CLICK HERE</a> <br/><br/>\r\n    Thank you,<br><br>\r\n    {FROM_NAME}\r\n    ', 'a:1:{s:4:"NAME";s:10:"Users Name";}', '1', 1, NULL, '2016-07-25', NULL);
