INSERT INTO `ucm_template` VALUES(40, 'invoice_payment_methods_offline', 'Displayed on the external invoice.', '<strong>Option #2: Pay Offline</strong>\r\n	        <br/>\r\n	        We support the following offline payment methods:\r\n	        <br/>\r\n	        ', 'a:0:{}', '0', 1, NULL, '2016-07-25', NULL);
