INSERT INTO `ucm_template` VALUES(42, 'invoice_payment_pending', 'Displayed when a user has an automatic payment pending on an invoice.', 'Automatic Invoice Payment Pending. <br/><br/>\r\n	        Thank you for your business.\r\n	        ', 'a:0:{}', '0', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(43, 'invoice_payment_thankyou', 'This page is displayed after a payment is made on an invoice.', '<h2>Invoice Payment</h2>\r\n	<p>Invoice Number: <strong>{INVOICE_NUMBER}</strong> </p>\r\n	<p>Thank you for making payment on this Invoice. We will email you a receipt shortly.</p>\r\n	', 'a:0:{}', '0', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(44, 'invoice_payment_subscription', 'Used when a customer tries to pay an invoice that has a subscription option.', '<h2>Payment for Invoice {INVOICE_NUMBER}</h2>\r\n<p>Please choose from the available payment options below:</p>\r\n<form action="{PAYMENT_URL}" method="post">\r\n<input type="hidden" name="invoice_payment_id" value="{INVOICE_PAYMENT_ID}">\r\n<input type="hidden" name="payment_method" value="{PAYMENT_METHOD}">\r\n<input type="hidden" name="payment_amount" value="{PAYMENT_AMOUNT}">\r\n<p><input type="submit" name="payment_single" value="Pay a Once Off amount of {PRETTY_PAYMENT_AMOUNT}"></p>\r\n<p><input type="submit" name="payment_subscription" value="Setup Automatic Payments of {PRETTY_PAYMENT_AMOUNT} every {SUBSCRIPTION_PERIOD}"></p>\r\n</form>\r\n', 'a:0:{}', '0', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(45, 'invoice_email_due', 'Invoice Owing: {INVOICE_NUMBER}', 'Dear {CUSTOMER_NAME},<br>\r\n<br>\r\nPlease find attached your invoice {INVOICE_NUMBER}.<br><br>\r\nThe {TOTAL_AMOUNT} is due on {DATE_DUE}.<br><br>\r\nYou can also view this invoice online by <a href="{INVOICE_URL}">clicking here</a>.<br><br>\r\nThank you,<br><br>\r\n{FROM_NAME}\r\n', 'a:6:{s:13:"CUSTOMER_NAME";s:14:"Customers Name";s:14:"INVOICE_NUMBER";s:14:"Invoice Number";s:12:"TOTAL_AMOUNT";s:23:"Total amount of invoice";s:8:"DATE_DUE";s:8:"Due Date";s:9:"FROM_NAME";s:9:"Your name";s:11:"INVOICE_URL";s:28:"Link to invoice for customer";}', '1', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(46, 'credit_note_email', 'Credit Note: {INVOICE_NUMBER}', 'Dear {CUSTOMER_NAME},<br>\r\n<br>\r\nPlease find attached your Credit Note {INVOICE_NUMBER} for Invoice {CREDIT_INVOICE_NUMBER}.<br><br>\r\nTotal amount: {TOTAL_AMOUNT}<br><br>\r\nYou can view this invoice online by <a href="{INVOICE_URL}">clicking here</a>.<br><br>\r\nThank you,<br><br>\r\n{FROM_NAME}\r\n', 'a:6:{s:13:"CUSTOMER_NAME";s:14:"Customers Name";s:14:"INVOICE_NUMBER";s:18:"Credit Note Number";s:21:"CREDIT_INVOICE_NUMBER";s:23:"Original Invoice Number";s:12:"TOTAL_AMOUNT";s:23:"Total amount of invoice";s:9:"FROM_NAME";s:9:"Your name";s:11:"INVOICE_URL";s:28:"Link to invoice for customer";}', '1', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(47, 'invoice_email_overdue', 'Invoice Overdue: {INVOICE_NUMBER}', 'Dear {CUSTOMER_NAME},<br>\r\n<br>\r\nThe attached invoice {INVOICE_NUMBER} is now <span style="font-weight:bold; color:#FF0000;">overdue</span>.<br><br>\r\nThe {TOTAL_AMOUNT} was due on {DATE_DUE}.<br><br>\r\nYou can also view this invoice online by <a href="{INVOICE_URL}">clicking here</a>.<br><br>\r\nThank you,<br><br>\r\n{FROM_NAME}\r\n', 'a:6:{s:13:"CUSTOMER_NAME";s:14:"Customers Name";s:14:"INVOICE_NUMBER";s:14:"Invoice Number";s:12:"TOTAL_AMOUNT";s:23:"Total amount of invoice";s:8:"DATE_DUE";s:8:"Due Date";s:9:"FROM_NAME";s:9:"Your name";s:11:"INVOICE_URL";s:28:"Link to invoice for customer";}', '1', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(48, 'invoice_email_paid', 'Invoice Paid: {INVOICE_NUMBER}', 'Dear {CUSTOMER_NAME},<br>\r\n<br>\r\nThank you for your {TOTAL_AMOUNT} payment on invoice {INVOICE_NUMBER}.<br><br>\r\nThis invoice was paid in full on {DATE_PAID}.<br><br>\r\nPlease find attached the receipt for this invoice payment. <br>\r\nYou can also view this invoice online by <a href="{INVOICE_URL}">clicking here</a>.<br><br>\r\nThank you,<br><br>\r\n{FROM_NAME}\r\n', 'a:6:{s:13:"CUSTOMER_NAME";s:14:"Customers Name";s:14:"INVOICE_NUMBER";s:14:"Invoice Number";s:12:"TOTAL_AMOUNT";s:23:"Total amount of invoice";s:9:"DATE_PAID";s:9:"Paid date";s:9:"FROM_NAME";s:9:"Your name";s:11:"INVOICE_URL";s:28:"Link to invoice for customer";}', '1', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(49, 'quote_external', 'Used when displaying the external view of a quote for approval.', '{HEADER}<h2>Quote Overview</h2>\r\nQuote Name: <strong>{QUOTE_NAME}</strong> <br/>\r\n{PROJECT_TYPE} Name: <strong>{PROJECT_NAME}</strong> <br/>\r\nCreate Date: <strong>{DATE_CREATE}</strong><br/>\r\nQuote Status: <strong>{if:DATE_APPROVED}Accepted on {DATE_APPROVED}{else}Pending{endif:DATE_APPROVED}</strong> <br/>\r\n{DESCRIPTION}\r\n<br/>\r\n{if:date_approved}\r\n<h2>Quote Has Been Accepted</h2>\r\n<p>Thank you, the quote was accepted by <strong>{APPROVED_BY}</strong> on <strong>{DATE_APPROVED}</strong>.</p>\r\n{else}\r\n<h2>Quote Approval Pending</h2>\r\n<p>If you would like to approve this quote please complete the form below:</p>\r\n<form action="" method="POST">\r\n<p>Your Name: <input type="text" name="quote_approve_name"> </p>\r\n<p><input type="checkbox" name="quote_approve_go" value="yes"> Yes, I approve this quote. </p>\r\n<p><input type="submit" name="quote_approve" value="Approve Quote" class="submit_button save_button"></p>\r\n</form>\r\n{endif:date_approved}\r\n\r\n<h2>Task List</h2> <br/>\r\n{TASK_LIST}\r\n', 'a:0:{}', '0', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(50, 'quote_pdf', 'Used for printing out an quote for the customer.', '<html>\r\n<head>\r\n<title>Quote</title>\r\n<meta http-equiv="Content-Type" content="text/html; charset=UTF-8">\r\n<style type="text/css">\r\nbody{\r\nfont-family:Helvetica, sans-serif;\r\npadding:0;\r\nmargin:0;\r\n}\r\ntd{\r\nfont-family:Helvetica, sans-serif;\r\npadding:2px;\r\n}\r\nh3{\r\nfont-size: 22px;\r\nfont-weight: bold;\r\nmargin:10px 0 10px 0;\r\npadding:0 0 5px 0;\r\nborder-bottom:1px solid #6f6f6f;\r\nwidth:100%;\r\n}\r\n.style11 {\r\nfont-size: 24px;\r\nfont-weight: bold;\r\nmargin:0;\r\npadding:0;\r\n}\r\n.task_header,\r\n.task_header th{\r\nbackground-color:#e8e8e8;\r\ncolor: #6f6f6f;\r\nfont-weight: bold;\r\n}\r\ntr.odd{\r\nbackground-color:#f9f9f9;\r\n}\r\n</style>\r\n</head>\r\n<body>\r\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td colspan="2" align="left" valign="top"><img title="Logo" src="http://ultimateclientmanager.com/images/logo_ucm.png" alt="Logo" width="202" height="60" /></td>\r\n<td colspan="2" align="left" valign="top"><span class="style11">QUOTE</span></td>\r\n</tr>\r\n<tr>\r\n<td width="12%">&nbsp;</td>\r\n<td width="43%">&nbsp;</td>\r\n<td width="14%">&nbsp;</td>\r\n<td width="31%">&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td><strong>ABN:</strong></td>\r\n<td>12 345 678 912</td>\r\n<td><strong>Quote No: <br /> </strong></td>\r\n<td>{QUOTE_NUMBER}</td>\r\n</tr>\r\n<tr>\r\n<td><strong>Email: </strong></td>\r\n<td>your@company.com</td>\r\n<td><strong>Issued Date:</strong></td>\r\n<td>{DATE_CREATE}</td>\r\n</tr>\r\n<tr>\r\n<td><strong>Web: </strong></td>\r\n<td>www.company.com</td>\r\n<td><strong>Valid Until:</strong></td>\r\n<td>{DATE_CREATE+30d}</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>RECIPIENT</h3>\r\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td valign="top" width="12%"><strong>Company:</strong></td>\r\n<td valign="top" width="43%">{CUSTOMER_NAME}</td>\r\n<td valign="top" width="14%"><strong>Email:</strong></td>\r\n<td valign="top" width="31%">{CONTACT_EMAIL}</td>\r\n</tr>\r\n<tr>\r\n<td valign="top"><strong>Contact:</strong></td>\r\n<td valign="top">{CONTACT_NAME}</td>\r\n<td valign="top"><strong>{PROJECT_TYPE}:</strong>&nbsp;</td>\r\n<td valign="top">{PROJECT_NAME}&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td valign="top"><strong>Phone:</strong></td>\r\n<td valign="top">{CONTACT_PHONE}</td>\r\n<td valign="top">&nbsp;</td>\r\n<td valign="top">&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>QUOTE DETAILS</h3>\r\n<div>{TASK_LIST}</div>\r\n<p>&nbsp;</p>\r\n<h3>QUOTE APPROVAL</h3>\r\n<p>{if:DATE_APPROVED}Thank you, this Quote was approved by <strong>{APPROVED_BY}</strong> on <strong>{DATE_APPROVED}</strong>{else} If you are happy with this quote please click the buttom below to process approval.</p>\r\n<p><a href="{QUOTE_LINK}">Approve This Quote</a>{endif:DATE_APPROVED}</p>\r\n</body>\r\n</html>', 'a:0:{}', '1', 1, NULL, '2016-07-25', NULL);
INSERT INTO `ucm_template` VALUES(51, 'quote_email', 'Quote: {QUOTE_NAME}', 'Dear {CUSTOMER_NAME},<br>\r\n<br>\r\nPlease find attached details on your quote: {QUOTE_NAME}.<br><br>\r\nYou can view and approve this quote online by <a href="{QUOTE_URL}">clicking here</a>.<br><br>\r\nThank you,<br><br>\r\n{FROM_NAME}\r\n', 'a:7:{s:13:"CUSTOMER_NAME";s:14:"Customers Name";s:10:"QUOTE_NAME";s:10:"Quote Name";s:12:"TOTAL_AMOUNT";s:21:"Total amount of quote";s:16:"TOTAL_AMOUNT_DUE";s:42:"Total amount of quote remaining to be paid";s:9:"FROM_NAME";s:9:"Your name";s:9:"QUOTE_URL";s:26:"Link to quote for customer";s:11:"QUOTE_TASKS";s:44:"Output of quote tasks similar to public link";}', '1', 1, NULL, '2016-07-25', NULL);
